testCases = [
    ["ABCABC", "ABC"],
    ["ABABAB", "ABAB"],
    ["LEET", "CODE"],
    ["ABCDEF", "ABC"],
    ["ABABABAB", "ABAB"],
    ["NLZGMNLZGMNLZGMNLZGMNLZGMNLZGMNLZGMNLZGM", "NLZGMNLZGMNLZGMNLZGMNLZGMNLZGMNLZGMNLZGMNLZGM"],
    ["", ""],
    ["AA", "A"]];
expectedResults = ["ABC", "AB", "", "", "ABAB", "NLZGM",
    "",
    "A"];


/**
 * @param {string} str1
 * @param {string} str2
 * @return {string}
 */
var gcdOfStrings = function (str1, str2) {
    if (str1 + str2 != str2 + str1)
        return "";
    for (let i = str1.length; i > 0; i--) {
        let substring = str1.substring(0, i);
        let matches1 = substring.length * (str1.split(substring).length - 1) == str1.length;
        if (matches1) {
            let matches2 = substring.length * (str2.split(substring).length - 1) == str2.length;
            if (matches2) {
                return substring;
            }
        }
    }
};


// # Testing
for (let i = 0; i < testCases.length; i++) {
    let result = gcdOfStrings(testCases[i][0], testCases[i][1]);
    if (result == expectedResults[i]) {
        console.log(`==Test case ${i + 1} was valid`)
        console.log(`✔️ Got "${result}", expected "${expectedResults[i]}"`)
    }
    else {
        console.log(`==Test case ${i + 1} was INVALID`)
        console.log(`❌ Got "${result}", expected "${expectedResults[i]}"`)
    }
}